const EventEmitter = require("events");
const _ = require("lodash");

/**
 * @extends EventEmitter
 */
export default class Collection extends EventEmitter {
	/**
	 * @param {Array} items
	 */
	constructor(items = []) {
		super();

		/**
		 * @type {Array}
		 * @private
		 */
		this.items = items;
	}

	/**
	 * Get an item in this collection by its index
	 * @param {Number} index
	 * @returns {*}
	 */
	at(index) {
		return this.items[index];
	}

	/**
	 * Remove an item from this collection by its index
	 * @param {Number} index
	 * @returns {Boolean} Whether the item was removed
	 */
	removeAt(index) {
		if (this.items[index] !== null && this.items[index] !== undefined) {
			this.emit("itemRemoved", this.items[index]);
			this.items.splice(index, 1);

			return true;
		}

		return false;
	}

	/**
	 * Add an item to this collection
	 * @param {*} item
	 */
	add(item) {
		this.items.push(item);
		this.emit("itemAdded", item);
	}

	/**
	 * Add an item to this collection only if it doesn't already exist
	 * @param {*} item
	 * @returns {Boolean} Whether the item was added
	 */
	addUnique(item) {
		if (!this.contains(item)) {
			this.add(item);

			return true;
		}

		return false;
	}

	/**
	 * Determine whether this collection contains an item
	 * @param {*} item
	 * @returns {Boolean}
	 */
	contains(item) {
		for (let i = 0; i < this.items.length; i++) {
			if (this.items[i] === item) {
				return true;
			}
		}

		return false;
	}

	/**
	 * Find an item in this collection
	 * @param {String} path
	 * @param {*} value
	 */
	find(path, value) {
		for (let i = 0; i < this.items.length; i++) {
			if (_.get(this.items[i], path) === value) {
				return this.items[i];
			}
		}

		return null;
	}
}

import Collection from "./collection";

const fs = require("fs");

/**
 * @extends Collection
 */
export default class EmojiCollection extends Collection {
	/**
	 * @param {Array<Object>} emojis
	 */
	constructor(emojis = []) {
		super(emojis);
	}

	/**
	 * @param {String} name
	 * @returns {String}
	 */
	get(name) {
		return this.find("name", name).id;
	}

	/**
	 * @param {String} path
	 * @returns {EmojiCollection}
	 */
	static fromFile(path) {
		if (!fs.existsSync(path)) {
			throw new Error(`[EmojiCollection.fromFile] Path does not exist: ${path}`);
		}

		return new EmojiCollection(JSON.parse(fs.readFileSync(path).toString()));
	}
}

/**
 * @enum {Number}
 * @readonly
 */
const AccessLevelType = {
	Guest: 0,
	Member: 1,
	Premium: 2,
	Moderator: 3,
	Admin: 4,
	Owner: 5,
	Developer: 6,

	/**
	 * @param {AccessLevelType} type
	 * @returns {String}
	 */
	toString(type) {
		switch (type) {
			case this.Guest:
				return "Guest";

			case this.Member:
				return "Member";

			case this.Premium:
				return "Premium";

			case this.Moderator:
				return "Moderator";

			case this.Admin:
				return "Admin";

			case this.Owner:
				return "Owner";

			case this.Developer:
				return "Developer";

			default:
				return "Unknown";
		}
	},

	/**
	 * @param {String} string
	 * @returns {AccessLevelType}
	 */
	fromString(string) {
		switch (string.toLowerCase()) {
			case "guest":
				return this.Guest;

			case "member":
				return this.Member;

			case "premium":
				return this.Premium;

			case "moderator":
				return this.Moderator;

			case "admin":
				return this.Admin;

			case "owner":
				return this.Owner;

			case "developer":
				return this.Developer;

			default:
				return -1;
		}
	}
};

export default AccessLevelType;

import Log from '../core/log';

export default class CommandArgumentParser {
	// TODO: Fully review, might contain bugs or be incomplete.
	/**
	 * @param {Object} rules
	 * @param {Object} args
	 * @param {Object} types
	 * @returns {Boolean}
	 */
	static validate(rules, args, types = {}) {
		const ruleKeys = Object.keys(rules);

		for (let ruleIdx = 0; ruleIdx < ruleKeys.length; ruleIdx++) {
			const ruleName = ruleKeys[ruleIdx]; // name:
			const rule = rules[ruleName];

			if (/^!?[a-zA-Z|_:]+$/.test(rule) === false) {
				throw new Error(`CommandArgumentParser.Validator: Invalid rule: ${rule}`);
			}

			const typeSplit = rules[ruleName].split("|"); // ["string", "number"]

			for (let typeIdx = 0; typeIdx < typeSplit.length; typeIdx++) {
				let type = typeSplit[typeIdx];

				Log.info(`Checking ${ruleName}@${typeSplit[typeIdx]}`);

				if (type.startsWith("!")) {
					if (typeSplit.length > 1 && typeIdx > 0) {
						throw new Error(`CommandArgumentParser.Validator: Having a required argument along with an OR operator is not allowed`);
					}
					else if (args[ruleName] == null || args[ruleName] === undefined) {
						Log.info("Failed: Important not present.");

						return false;
					}

					type = type.substring(1);
				}

				if (args[ruleName]) {
					if (type[0] === ":") {
						const actualType = type.substring(1);

						if (!Object.keys(types).includes(actualType)) {
							throw new Error(`CommandArgumentParser.Validator: Custom argument type not registered: ${actualType}`);
						}
						// TODO: Check if it's a Regex instead of just checking method
						else if (types[actualType] instanceof RegExp) {
							break;
						}
						else if (typeof types[actualType] === "function" && types[actualType](args[ruleName])) {
							break;
						}
						else if (typeIdx === typeSplit.length - 1) {
							Log.info("Failed: check on CUSTOM-type first or last item not passed");

							return false;
						}
					}
					else if (typeof args[ruleName] === type) {
						break;
					}
					else if (typeIdx === typeSplit.length - 1) {
						Log.info("Failed: check on PRIMAL-type first or last item not passed");

						return false;
					}
				}
				else {
					break;
				}
			}
		}

		return true;
	}

	/**
	 * @param {String} type
	 * @param {String} arg
	 * @param {Object} resolvers
	 * @returns {*}
	 */
	static resolve(type, arg, resolvers) {
		const keys = Object.keys(resolvers);

		for (let i = 0; i < keys.length; i++) {
			if (keys[i] === type) {
				return resolvers[keys[i]](arg);
			}
		}

		throw new Error(`[CommandArgumentParser] Missing argument resolver: ${type}`);
	}
}

/**
 * @enum {Number}
 * @readonly
 */
const CommandCategoryType = {
	General: 0,
	Fun: 1,
	Utility: 2,
	NSFW: 3,
	Moderation: 4,
	Music: 5,
	Economy: 6,
	Developer: 7
};

export default CommandCategoryType;

import EditableMessage from "../message/editable-message";
import EmbedBuilder from "../message/embed-builder";

const Discord = require("discord.js");

export default class CommandExecutionContext {
	/**
	 * @param {Message} message
	 * @param {Array<String>} args
	 * @param {Bot} bot
	 * @param {AccessLevelType} accessLevel
	 * @param {EmojiCollection} emojis
	 */
	constructor(message, args, bot, accessLevel, emojis) {
		/**
		 * @type {Message}
		 * @readonly
		 */
		this.message = message;

		/**
		 * @type {Array<String>}
		 * @readonly
		 */
		this.arguments = args;

		/**
		 * @type {Bot}
		 * @readonly
		 */
		this.bot = bot;

		/**
		 * @type {AccessLevelType}
		 * @readonly
		 */
		this.accessLevel = accessLevel;

		/**
		 * @type {EmojiCollection}
		 * @readonly
		 */
		this.emojis = emojis;
	}

	/**
	 * @param {*} stream
	 * @param {String} name
	 * @returns {(Promise<EditableMessage>|Null)}
	 */
	async fileStream(stream, name) {
		return await this.message.channel.send(new Discord.Attachment(stream, name));
	}

	/**
	 * @param {(Object|EmbedBuilder)} content
	 * @param {Boolean} [autoDelete=false]
	 * @returns {(Promise<EditableMessage>|null)}
	 */
	async respond(content, autoDelete = false) {
		let embed = null;

		if (content.text) {
			if (content.text.toString().trim() === "" || content.text === undefined || content.text === null) {
				content.text = ":thinking: *Empty response.*";
			}
		}

		if (content instanceof EmbedBuilder) {
			embed = content;
		}
		else {
			if (!content.color) {
				content.color = "GREEN";
			}

			if (!content.footer) {
				content.footer = {
					text: `Requested by ${this.sender.username}`,
					icon: this.sender.avatarURL
				};
			}

			embed = EmbedBuilder.fromObject(content);
		}

		const messageResult = await this.message.channel.send(embed.build()).catch((error) => {
			// TODO: Temporarily disabled due to spamming on unwanted servers.
			// this.privateReply(`Oh noes! For some reason, I was unable to reply to you in that channel. (${error.message})`);
		});

		if (autoDelete && messageResult) {
			// TODO: Cannot access .length in embeds
			// also static time for images, probably need function
			const timeInSeconds = (4000 + (100 * embed.build() * 1000)) / 1000;

			messageResult.delete(4000 + (100 * messageResult.content.length * 1000));

			// TODO
			// this.bot.log.info(messageResult.content.length);
			// this.bot.log.info(`time : ${timeInSeconds}`);
		}

		return (messageResult !== undefined ? new EditableMessage(messageResult) : null);
	}

	/**
	 * @param {Snowflake} userId
	 * @returns {AccessLevelType}
	 */
	getAuth(userId) {
		return this.bot.commands.getAuthority(this.message.guild.id, this.message.guild.member(userId).roles.array().map((role) => role.name), userId);
	}

	/**
	 * @returns {AccessLevelType}
	 */
	get auth() {
		return this.getAuth(this.sender.id);
	}

	/**
	 * @param {Object} sections
	 * @param {String} color
	 * @returns {Promise<EditableMessage>}
	 */
	async sections(sections, color = "GREEN") {
		return await this.respond(EmbedBuilder.sections(sections, color));
	}

	/**
	 * @param {String} text
	 * @returns {Promise<EditableMessage>}
	 */
	async ok(text) {
		return await this.respond({
			text: `${this.emojis.get("check")} ${text}`
		});
	}

	/**
	 * @param {String} text
	 * @returns {Promise<EditableMessage>}
	 */
	async loading(text) {
		return await this.respond({
			text: `${this.emojis.get("loading")} ${text}`,
			color: "BLUE"
		});
	}

	/**
	 * @param {String} text
	 * @returns {Promise<EditableMessage>}
	 */
	async fail(text) {
		return await this.respond({
			text: `:thinking: ${text}`,
			color: "RED"
		}, true);
	}

	/**
	 * @returns {User}
	 */
	get sender() {
		return this.message.author;
	}

	/**
	 * @param {String} message
	 * @returns {(Promise<Message>|null)}
	 */
	async reply(message) {
		return await this.message.reply(message);
	}

	/**
	 * @param {String} message
	 */
	async privateReply(message) {
		return await this.message.author.send(message);
	}
}

import Command from "./command";
import Log from "../core/log";

const fs = require("fs");
const path = require("path");
const Typer = require("@raxor1234/typer");

export default class CommandLoader {
	/**
	 * @param {CommandManager} commandManager
	 */
	constructor(commandManager) {
		/**
		 * @type {CommandManager}
		 * @private
		 */
		this.commandManager = commandManager;
	}

	/**
	 * Load all the commands from path
	 * @returns {Promise}
	 */
	async loadAll() {
		return new Promise((resolve) => {
			fs.readdir(this.commandManager.commandsPath, (error, files) => {
				files.forEach((file) => {
					if (!file.startsWith("@")) {
						const modulePath = path.join(this.commandManager.commandsPath, path.basename(file, ".js"));

						let module = require(modulePath);

						// Support for ES6 compiled
						if (module.default && typeof module.default === "object") {
							module = module.default;
						}

						if (CommandLoader.validate(module)) {
							this.commandManager.register(Command.fromModule(module));
						}
						else {
							Log.warn(`Skipping invalid command: ${path.basename(file, ".js")}`);
						}
					}
					else {
						Log.verbose(`Skipping command: ${path.basename(file, ".js")}`);
					}
				});

				Log.verbose(`Loaded a total of ${files.length} commands`);
				resolve();
			});
		});
	}

	load(pth) {
		// TODO
	}

	/**
	 * @param {Object} module
	 * @returns {Boolean} Whether the module is valid
	 */
	static validate(module) {
		Typer.validate({
			executed: "function",
			canExecute: "function",
			meta: "object"
		}, module);

		Typer.validate({
			name: "string",
			description: "string",
			accessLevel: "number",
			aliases: "object",
			maxArguments: "number"
		}, module.meta);

		// TODO
		return true;
	}
}

import AccessLevelType from "../commands/access-level-type";
import CommandArgumentParser from "./command-argument-parser";
import CommandExecutedEvent from "../events/command-executed-event";
import CommandCategoryType from "./command-category-type";
// import Collection from "../core/collection";

const fs = require("fs");

export default class CommandManager /* extends Collection */ {
	/**
	 * @param {Bot} bot
	 * @param {String} commandsPath
	 * @param {String} accessLevelsPath
	 * @param {Object} argumentTypes
	 */
	constructor(bot, commandsPath, accessLevelsPath, argumentTypes) {
		/**
		 * @type {Bot}
		 * @private
		 * @readonly
		 */
		this.bot = bot;

		/**
		 * @type {String}
		 * @private
		 * @readonly
		 */
		this.commandsPath = commandsPath;

		/**
		 * @type {String}
		 * @private
		 * @readonly
		 */
		this.accessLevelsPath = accessLevelsPath;

		/**
		 * @type {Object}
		 * @readonly
		 */
		this.argumentTypes = argumentTypes;

		/**
		 * @type {Array<Command>}
		 * @private
		 */
		this.commands = [];

		/**
		 * @type {Object}
		 * @private
		 */
		this.accessLevels = [];

		fs.readFile(this.accessLevelsPath, (error, data) => {
			// TODO: Validate access levels
			this.accessLevels = JSON.parse(data.toString());
		});
	}

	/**
	 * @param {Command} command
	 */
	register(command) {
		this.commands.push(command);
	}

	/**
	 * @param {String} commandBase
	 * @returns {Boolean}
	 */
	removeByBase(commandBase) {
		return this.remove(this.getByBase(commandBase));
	}

	/**
	 * @param {Command} command
	 * @returns {Boolean}
	 */
	remove(command) {
		return this.removeAt(this.commands.indexOf(command));
	}

	/**
	 * @param {Number} index
	 * @returns {Boolean}
	 */
	removeAt(index) {
		if (this.commands[index]) {
			this.commands.splice(index, 1);

			return true;
		}

		return false;
	}

	/**
	 * @param {String} commandBase
	 * @returns {Boolean}
	 */
	contains(commandBase) {
		return this.getByBase(commandBase) !== null;
	}

	/**
	 * @param {Array<Command>} commands
	 */
	registerMultiple(commands) {
		for (let i = 0; i < commands.length; i++) {
			this.register(commands[i]);
		}
	}

	/**
	 * @param {String} commandBase
	 * @returns {Boolean}
	 */
	isRegistered(commandBase) {
		return this.getByBase(commandBase) != null;
	}

	/**
	 * @param {String} commandBase
	 * @returns {(Command|Null)}
	 */
	getByBase(commandBase) {
		for (let i = 0; i < this.commands.length; i++) {
			if (this.commands[i].base === commandBase || this.commands[i].aliases.includes(commandBase)) {
				return this.commands[i];
			}
		}

		return null;
	}

	/**
	 * @param {Snowflake} guildId
	 * @param {String} role
	 * @returns {AccessLevelType}
	 */
	getAccessLevelByRole(guildId, role) {
		const accessLevels = this.bot.dataAdapter.get("access-levels", guildId);
		const keys = Object.keys(accessLevels);

		for (let keyIndex = 0; keyIndex < keys.length; keyIndex++) {
			for (let roleIndex = 0; roleIndex < accessLevels[keys[keyIndex]].length; roleIndex++) {
				if (accessLevels[keys[keyIndex]][roleIndex] === role) {
					return AccessLevelType.fromString(keys[keyIndex]);
				}
			}
		}

		return null;
	}

	/**
	 * @param {Snowflake} userId
	 * @returns {Boolean}
	 */
	isDeveloper(userId) {
		return this.bot.dataAdapter.get("global.developers").includes(userId);
	}

	/**
	 * @param {Snowflake} guildId
	 * @param {Snowflake} userId
	 * @returns {AccessLevelType}
	 */
	getAccessLevelById(guildId, userId) {
		if (this.isDeveloper(userId)) {
			return AccessLevelType.Developer;
		}

		const accessLevels = this.bot.dataAdapter.get("access-levels", guildId);
		const keys = Object.keys(accessLevels);

		for (let keyIndex = 0; keyIndex < keys.length; keyIndex++) {
			// TODO: Use index of instead of looping
			for (let roleIndex = 0; roleIndex < accessLevels[keys[keyIndex]].length; roleIndex++) {
				const value = accessLevels[keys[keyIndex]][roleIndex];

				if (!isNaN(value) && value === userId.toString()) {
					return AccessLevelType.fromString(keys[keyIndex]);
				}
			}
		}

		return null;
	}

	// TODO: Move to the corresponding file/class
	/**
	 * @param {Discord.Message} message
	 * @param {String} role
	 * @returns {Boolean}
	 */
	hasRole(message, role) {
		return message.member.roles.find("name", role) !== null;
	}

	/**
	 * @param {Discord.Message} message
	 * @param {Array<String>} roles
	 * @returns {Boolean}
	 */
	hasRoles(message, roles) {
		for (let i = 0; i < roles.length; i++) {
			if (!this.hasRole(message, roles[i])) {
				return false;
			}
		}

		return true;
	}

	/**
	 * @param {Snowflake} guildId
	 * @param {Array<String>} roles
	 * @returns {AccessLevelType}
	 */
	getHighestAccessLevelByRoles(guildId, roles) {
		let highest = AccessLevelType.Guest;

		for (let i = 0; i < roles.length; i++) {
			const accessLevel = this.getAccessLevelByRole(guildId, roles[i]);

			if (accessLevel > highest) {
				highest = accessLevel;
			}
		}

		return highest;
	}

	/**
	 * @param {Snowflake} guildId
	 * @param {Message} message
	 * @param {AccessLevelType} accessLevel
	 * @returns {Boolean}
	 */
	hasAuthority(guildId, message, accessLevel) {
		return this.getAuthority(guildId, message.member.roles.array().map((role) => role.name), message.author.id) >= accessLevel;

		// TODO: Replaced by getAuthority() method
		// return (this.getHighestAccessLevelByRoles(message.member.roles.array().map((role) => role.name)) >= accessLevel) || (this.getAccessLevelById(message.author.id) >= accessLevel);
	}

	/**
	 * @param {Snowflake} guildId
	 * @param {Array<String>} roles
	 * @param {Snowflake} userId
	 * @returns {AccessLevelType} The authority of the user
	 */
	getAuthority(guildId, roles = ["@everyone"], userId) {
		const byRoles = this.getHighestAccessLevelByRoles(guildId, roles);
		const byId = this.getAccessLevelById(guildId, userId);

		if (byRoles > byId) {
			return byRoles;
		}

		return byId;
	}

	/**
	 * @param {Object} rules
	 * @param {Array<String>} args
	 * @returns {Object} The assembled arguments
	 */
	assembleArguments(rules, args) {
		const result = {};

		if (rules.length !== args.length) {
			this.bot.log.info("AssembleArguments: Not same length");
		}

		this.bot.log.info(args);

		for (let i = 0; i < rules.length; i++) {
			result[rules[i]] = (isNaN(args[i]) ? args[i] : parseInt(args[i]));
		}

		return result;
	}

	/**
	 * @param {CommandExecutionContext} context
	 * @param {Command} command The command to handle
	 * @returns {Promise<Boolean>} Whether the command was successfully executed
	 */
	async handle(context, command) {
		if (!context.message.member) {
			context.message.channel.send("That command must be used in a text channel. Sorry!");
		}
		else if (command.category === CommandCategoryType.NSFW && !context.message.channel.nsfw) {
			context.fail(":underage: Please use an NSFW channel for this command.");
		}
		else if (!command.isEnabled) {
			await context.fail("That command is disabled and may not be used.");
		}
		else if (!this.hasAuthority(context.message.guild.id, context.message, command.accessLevel)) {
			const minAuthority = AccessLevelType.toString(command.accessLevel);
			context.fail(`You don't have the authority to use that command. You must be at least a(n) **${minAuthority}**.`);
		}
		else if (context.arguments.length > command.maxArguments) {
			if (command.maxArguments > 0) {
				context.fail(`That command only accepts up to **${command.maxArguments}** arguments.`);
			}
			else {
				context.fail(`That command does not accept any arguments.`);
			}
		}
		else if (!command.canExecute(context)) {
			context.fail("That command cannot be executed right now.");
		}
		else if (!CommandArgumentParser.validate(command.args, this.assembleArguments(Object.keys(command.args), context.arguments), this.argumentTypes)) {
			await context.fail("Invalid argument usage. Please use the `usage` command.");
		}
		else {
			try {
				const result = command.executed(context); // .catch((error) => context.respond(`There was an error while executing that command. (${error.message})`, "", "RED"));
				context.bot.emit("commandExecuted", new CommandExecutedEvent(command, context));

				return result;
			}
			catch (error) {
				// TODO: Use the Log class
				console.error(error);
				context.fail(`**Oh noes!** There was an error executing that command. (${error.message})`);
			}
		}

		return false;
	}
}

export default class CommandParser {
	/**
	 * @param {String} commandString
	 * @param {CommandManager} manager
	 * @param {String} trigger
	 * @returns {*}
	 */
	static parse(commandString, manager, trigger) {
		if (this.isValid(commandString, manager, trigger)) {
			return manager.getByBase(this.getCommandBase(commandString, trigger));
		}

		return null;
	}

	/**
	 * @param {String} commandString
	 * @param {CommandManager} manager
	 * @param {String} trigger
	 * @returns {Boolean}
	 */
	static isValid(commandString, manager, trigger) {
		if (commandString.startsWith(trigger)) {
			return manager.isRegistered(this.getCommandBase(commandString, trigger));
		}

		return false;
	}

	/**
	 * @param {String} commandString
	 * @param {String} trigger
	 * @returns {*}
	 */
	static getCommandBase(commandString, trigger) {
		const regexResult = new RegExp(`^${trigger}([a-zA-Z]+)`).exec(commandString);

		if (regexResult) {
			return regexResult[1];
		}

		return null;
	}

	/**
	 *
	 * @param {String} commandString
	 * @returns {Array<String>}
	 */
	static getArguments(commandString) {
		const expression = / (```((?!```).)*```|"[^"]+"|'[^']+'|`[^`]+`|[^ ]+)/g;
		const argCleanExpression = /(```|`|'|"|)(.+)\1/;
		const result = [];

		let match = expression.exec(commandString);

		while (match != null) {
			result.push(argCleanExpression.exec(match[1])[2]);
			match = expression.exec(commandString);
		}

		return result;
	}

	// TODO: Also take in arg schema to avoid matching accidental args.
	/**
	 * @param {Array<String>} args
	 * @param {Object} types
	 * @param {Object} resolvers
	 * @returns {Array<String>} The resolved arguments
	 */
	static resolveArguments(args, types, resolvers) {
		const result = args;
		const typeKeys = Object.keys(types);

		for (let argIdx = 0; argIdx < result.length; argIdx++) {
			for (let typeIdx = 0; typeIdx < typeKeys.length; typeIdx++) {
				let match = false;

				if (typeof types[typeKeys[typeIdx]] === "function") {
					match = types[typeKeys[typeIdx]](args[argIdx]);
				}
				else if (types[typeKeys[typeIdx]] instanceof RegExp) {
					match = types[typeKeys[typeIdx]].test(args[argIdx]);
				}

				if (match) {
					if (typeof resolvers[typeKeys[typeIdx]] === "function") {
						result[argIdx] = resolvers[typeKeys[typeIdx]](result[argIdx]);
					}
					// TODO: Issue further testing, there's a chance this is a bug.
					/* else {
						Log.error(`[CommandParser.resolveArguments] Expecting function but got '${typeof resolvers[typeKeys[typeIdx]]}'.`);
					} */
				}
			}
		}

		return result;
	}
}

export default class Command {
	/**
	 * @param {String} base
	 * @param {String} description
	 * @param {Array<String>} aliases
	 * @param {Number} maxArguments
	 * @param {AccessLevelType} accessLevel
	 * @param {Function} onExecuted
	 * @param {Function} canExecute
	 * @param {CommandCategoryType} category
	 * @param {Object} args
	 * @param {Boolean} isEnabled
	 */
	constructor(base, description, aliases, maxArguments, accessLevel, onExecuted, canExecute, category, args, isEnabled) {
		/**
		 * @type {String}
		 * @readonly
		 */
		this.base = base;

		/**
		 * @type {String}
		 * @readonly
		 */
		this.description = description;

		/**
		 * @type {Array<String>}
		 * @readonly
		 */
		this.aliases = aliases;

		/**
		 * @type {Number}
		 * @readonly
		 */
		this.maxArguments = maxArguments;

		/**
		 * @type {AccessLevelType}
		 * @readonly
		 */
		this.accessLevel = accessLevel;

		/**
		 * @type {Function}
		 * @readonly
		 */
		this.executed = onExecuted;

		/**
		 * @type {Function}
		 * @readonly
		 */
		this.canExecute = canExecute;

		/**
		 * @type {CommandCategoryType}
		 * @readonly
		 */
		this.category = category;

		/**
		 * @type {Array<String>}
		 * @readonly
		 */
		this.args = args;

		/**
		 * @type {Boolean}
		 * @readonly
		 */
		this.isEnabled = isEnabled;
	}

	/**
	 * @param {Object} module
	 * @returns {Command}
	 */
	static fromModule(module) {
		return new Command(
			module.meta.name,
			module.meta.description,
			module.meta.aliases,
			module.meta.maxArguments,
			module.meta.accessLevel,
			module.executed,
			module.canExecute,
			module.meta.category,
			module.meta.args,
			module.meta.enabled
		);
	}
}

