import Log from "../core/log";

const readline = require("readline");

export default class ConsoleInterface {
	/**
	 * @param {Bot} bot
	 */
	init(bot) {
		const ci = readline.createInterface({
			input: process.stdin,
			output: process.stdout
		});

		ci.prompt(true);

		ci.on("line", (input) => {
			switch (input.trim()) {
				case "": {
					break;
				}

				case "stop": {
					bot.disconnect();
					process.exit(0);

					break;
				}

				case "help": {
					console.log("CLI Commands: stop, help");

					break;
				}

				default: {
					console.log(`Invalid command: ${input}`);
				}
			}

			ci.prompt();
		});

		ci.on("close", () => {
			// TODO: Temp. disabled due to interferring and going straight disconnection on vps/linux
			/* bot.disconnect();
			process.exit(0); */
		});

		Log.verbose("Console interface setup completed");
	}
}

import CommandParser from "../commands/command-parser";
import CommandExecutionContext from "../commands/command-execution-context";
import ConsoleInterface from "../console/console-interface";
import EmojiMenuManager from "../emoji-ui/emoji-menu-manager";
import CommandManager from "../commands/command-manager";
import Utils from "./utils";
import EmojiCollection from "../collections/emoji-collection";
import Settings from "./settings";
import FeatureManager from "../features/feature-manager";
import CommandLoader from "../commands/command-loader";
import Log from "./log";
import Schema from "../schema/schema";
import DataAdapter from "../data-adapters/data-adapter";

const Discord = require("discord.js");
const EventEmitter = require("events");
const fs = require("fs");

/**
 * @extends EventEmitter
 */
export default class Bot extends EventEmitter {
	/**
	 * @param {Object} data
	 */
	constructor(data) {
		super();

		// Setup the class
		this.setup(data);
	}

	/**
	 * Setup the bot from an object
	 * @param {Object} data
	 * @return {Promise}
	 */
	async setup(data) {
		// TODO: Should use the Typer library (Typer needs update)
		if (!Schema.validate(data, {
			"paths.settings": "string",
			"paths.emojis": "string",
			"paths.commands": "string",
			"paths.accessLevels": "string",
			argumentTypes: "object",
			dataAdapter: DataAdapter
		})) {
			Log.throw("[Bot.setup] Invalid data provided.");
		}

		/**
		 * @type {Settings}
		 * @readonly
		 */
		this.settings = new Settings(data.paths.settings);

		/**
		 * @type {DataAdapter}
		 * @readonly
		 */
		this.dataAdapter = data.dataAdapter;

		/**
		 * @type {EmojiCollection}
		 * @readonly
		 */
		this.emojis = EmojiCollection.fromFile(data.paths.emojis);

		/**
		 * @type {Discord.Client}
		 * @readonly
		 */
		this.client = new Discord.Client();

		/**
		 * @type {CommandManager}
		 * @readonly
		 */
		this.commands = new CommandManager(this, data.paths.commands, data.paths.accessLevels, data.argumentTypes);

		/**
		 * @type {FeatureManager}
		 * @readonly
		 */
		this.features = new FeatureManager();

		/**
		 * @type {CommandLoader}
		 * @readonly
		 */
		this.commandLoader = new CommandLoader(this.commands);

		/**
		 * @type {ConsoleInterface}
		 * @readonly
		 */
		this.console = new ConsoleInterface();

		/**
		 * @type {EmojiMenuManager}
		 * @readonly
		 */
		this.menus = new EmojiMenuManager(this.client);

		/**
		 * @type {Log}
		 * @readonly
		 */
		this.log = new Log(this);

		// Setup the Discord client's events
		this.setupEvents();

		// Load commands
		await this.commandLoader.loadAll();
		Log.verbose("Bot setup completed");
	}

	/**
	 * Setup the client's events
	 */
	setupEvents() {
		// TODO: Find better position
		const resolvers = {
			user: (arg) => Utils.resolveId(arg),
			channel: (arg) => Utils.resolveId(arg),
			role: (arg) => Utils.resolveId(arg),
			state: (arg) => Utils.translateState(arg)
		};

		// Discord client events
		this.client.on("ready", () => {
			this.log.info("Ready");
			this.console.init(this);
		});

		this.client.on("message", async (message) => {
			if (!message.author.bot) {
				if (CommandParser.isValid(message.content, this.commands, this.settings.general.prefix)) {
					this.commands.handle(
						new CommandExecutionContext(
							message,
							CommandParser.resolveArguments(CommandParser.getArguments(message.content), this.commands.argumentTypes, resolvers),
							this,
							this.commands.getAuthority(message.guild.id, message.member.roles.array().map((role) => role.name), message.author.id),
							this.emojis
						),

						CommandParser.parse(
							message.content,
							this.commands,
							this.settings.general.prefix
						)
					);
				}
				else if (message.content === "?prefix") {
					message.channel.send(`Command prefix: **${this.settings.general.prefix}**`);
				}
			}
		});
	}

	/**
	 * Connect the client
	 * @return {Promise}
	 */
	async connect() {
		this.log.info("Starting");
		await this.client.login(this.settings.general.token);
	}

	/**
	 * Restart the bot's client
	 * @return {Promise}
	 */
	async restart() {
		this.log.info("Restarting");

		// TODO: Actually reload all the features and commands
		// this.features.reloadAll(this);

		await this.disconnect();
		await this.connect();
	}

	/**
	 * Disconnect the client
	 * @return {Promise}
	 */
	async disconnect() {
		this.settings.save();
		await this.client.destroy();
		this.log.info("Disconnected");
	}

	/**
	 * Clear all the files inside the temp folder
	 * @return {Promise}
	 */
	static async clearTemp() {
		if (fs.existsSync("./temp")) {
			fs.readdir("./temp", (error, files) => {
				for (let i = 0; i < files.length; i++) {
					fs.unlink(`./temp/${files[i]}`);
				}
			});
		}
	}
}

const colors = require("colors");
const fs = require("fs");

export default class Log {
	/**
	 * @param {Bot} bot
	 * @param {Boolean} debug
	 * @param {Boolean} verbose
	 */
	constructor(bot, debug = false, verbose = false) {
		/**
		 * @type {Bot}
		 * @private
		 * @readonly
		 */
		this.bot = bot;

		/**
		 * @type {Boolean}
		 * @private
		 * @readonly
		 */
		this.debugMode = debug;

		/**
		 * @type {Boolean}
		 * @private
		 * @readonly
		 */
		this.verboseMode = verbose;

		Log.instance = this;
	}

	/**
	 * @param {String} message
	 * @param {String} color
	 * @param {String} prefix
	 * @param {String} throwMsg
	 */
	log(message, color = "white", prefix = null, throwMsg = false) {
		Log.log(message, color, prefix, throwMsg);
	}

	info(message) {
		this.log(message, "cyan", "info");
	}

	success(message) {
		this.log(message, "green", "sucs");
	}

	warn(message) {
		this.log(message, "yellow", "warn");
	}

	error(message) {
		this.log(message, "red", "dang");
	}

	verbose(message) {
		if (this.verboseMode) {
			this.log(message, "gray");
		}
	}

	debugMode(message) {
		if (this.debugMode) {
			this.log(message, "magenta", "dbug");
		}
	}

	channel(content, options) {
		const guildLog = this.bot.dataAdapter.get("global.guild-log");
		if (!guildLog.enabled) {
			return;
		}

		this.bot.client.guilds.get(guildLog.guild).channels.get(guildLog.channel).send(content, options);
	}

	/**
	 * @param {String} message
	 * @param {String} color
	 * @param {String} prefix
	 */
	static async log(message, color = "white", prefix = null) {
		const date = new Date().toISOString().replace(/T/, " ").replace(/\..+/, "");

		// TODO: Make this next line work on the vps
		// process.stdout.write(`\x1B[2D[${date}] ${colors[color](message)}\n> `);
		console.log(`[${date}] ${colors[color](message)}`);

		if (prefix !== null) {
			message = `<${prefix.toUpperCase()}> ${message}`;
		}
		fs.writeFile("bot.log", `[${date}] ${message}\n`, { flag: "a" }, (err) => {
			if (err) throw err;
		});
	}

	/**
	 * @param {String} message
	 */
	static info(message) {
		Log.log(message, "cyan", "info");
	}

	/**
	 * @param {String} message
	 */
	static success(message) {
		Log.log(message, "green", "sucs");
	}

	/**
	 * @param {String} message
	 */
	static warn(message) {
		Log.log(message, "yellow", "warn");
	}

	/**
	 * @param {String} message
	 */
	static error(message) {
		Log.log(message, "red", "dang");
	}

	/**
	 * @param {String} message
	 */
	static throw(message) {
		Log.log(message, "red", "dang");
		process.exit(1);
	}

	/**
	 * @param {String} message
	 */
	static verbose(message) {
		if (Log.instance !== null) {
			if (Log.instance.verboseMode) {
				Log.log(message, "grey");
			}
		} else {
			Log.log(message, "grey");
		}
	}

	/**
	 * @param {String} message
	 */
	static debug(message) {
		if (Log.instance !== null) {
			if (Log.instance.debugMode) {
				Log.log(message, "magenta", "dbug");
			}
		} else {
			Log.log(message, "magenta", "dbug");
		}
	}
}

