import Log from "./log";

const fs = require("fs");

export default class Settings {
	/**
	 * @param {String} path
	 */
	constructor(path) {
		if (fs.existsSync(path)) {
			/**
			 * @type {String}
			 * @private
			 * @readonly
			 */
			this.path = path;

			const jsonObj = JSON.parse(fs.readFileSync(path).toString());

			// TODO: Should be automatic
			// TODO: Should be validated using Schema
			/**
			 * @type {Object}
			 * @readonly
			 */
			this.general = jsonObj.general;

			/**
			 * @type {Object}
			 * @readonly
			 */
			this.keys = jsonObj.keys;

			/**
			 * @type {Object}
			 * @readonly
			 */
			this.log = jsonObj.log;
		}
		else {
			Log.error("Could not load settings: File does not exist");
		}
	}

	/**
	 * Save the local settings into path
	 */
	save() {
		fs.writeFileSync(this.path, JSON.stringify({
			general: this.general,
			log: this.log,
			keys: this.keys
		}, null, 4));
	}

	// TODO
	validate() {
		if (this.general.token === null) {
			Log.error("[Settings] Token cannot be null or empty");
		}
	}
}

import Log from "./log";

const fs = require("fs");
const _ = require("lodash");

export default class UserConfig {
	/**
	 * @param {String} path
	 */
	constructor(path) {
		if (fs.existsSync(path)) {
			/**
			 * @type {String}
			 * @private
			 * @readonly
			 */
			this.path = path;

			/**
			 * @type {any}
			 * @private
			 */
			this.config = JSON.parse(fs.readFileSync(path).toString());
		}
		else {
			Log.error("Could not load user config: File does not exist");
		}
	}

	/**
	 * @param {String} path
	 * @param {*} value
	 * @param {(Snowflake|Null)} guildId
	 * @param {String} template
	 */
	set(path, value, guildId = null, template = "default") {
		const finalPath = guildId ? `${guildId}.${path}` : path;

		Log.info(finalPath);

		if (this.get(`${template}.${path}`, guildId) === value) {
			_.unset(this.config, finalPath);
		}
		else {
			_.set(this.config, finalPath, value);
			Log.info(`set ${finalPath} to ${value}`);
		}

		this.save();
	}

	/**
	 * @param {String} path
	 * @param {(Snowflake|Null)} guildId
	 * @param {String} template
	 * @returns {*}
	 */
	get(path, guildId = null, template = "default") {
		const finalPath = guildId ? `${guildId}.${path}` : path;

		if (!this.contains(path, guildId)) {
			return _.get(this.config, `${template}.${path}`);
		}

		return _.get(this.config, finalPath);
	}

	/**
	 * @param {String} path
	 * @param {*} item
	 * @param {(Snowflake|Null)} guildId
	 */
	push(path, item, guildId = null) {
		const items = this.get(path, guildId).slice(0);

		items.push(item);
		this.set(path, items, guildId);
	}

	/**
	 * @param {String} path
	 * @param {(Snowflake|Null)} guildId
	 * @returns {boolean}
	 */
	contains(path, guildId = null) {
		const finalPath = guildId ? `${guildId}.${path}` : path;

		return _.has(this.config, finalPath);
	}

	/**
	 * @param {Snowflake} id
	 */
	createGuild(id) {
		this.set(id, {});
		this.save();
	}

	/**
	 * @param {Snowflake} id
	 */
	removeGuild(id) {
		delete this.config[id];
		this.save();
	}

	save() {
		fs.writeFileSync(this.path, JSON.stringify(this.config, null, 4));
	}
}

import TimeAgo from "javascript-time-ago";
import en from "javascript-time-ago/locale/en";

TimeAgo.locale(en);

const timeAgo = new TimeAgo("en-US");

export default class Utils {
	/**
	 * @param {String} mention
	 * @returns {String}
	 */
	static resolveId(mention) {
		return mention
			.replace("<", "")
			.replace(">", "")
			.replace("@", "")
			.replace("!", "")
			.replace("&", "")
			.replace("#", "");
	}

	/**
	 * @param {Number} min
	 * @param {Number} max
	 * @returns {Number}
	 */
	static getRandomInt(min, max) {
		return Math.floor(Math.random() * max) + min;
	}

	/**
	 * @param {Array} array
	 * @returns {Array}
	 */
	static shuffle(array) {
		let counter = array.length;

		// While there are elements in the array
		while (counter > 0) {
			// Pick a random index
			const index = Math.floor(Math.random() * counter);

			// Decrease counter by 1
			counter--;

			// And swap the last element with it
			const temp = array[counter];

			array[counter] = array[index];
			array[index] = temp;
		}

		return array;
	}

	/**
	 * @param {Object} options
	 * @param {User} requester
	 * @param {Discord.Channel} channel
	 * @param {String} [footerSuffix=""]
	 * @returns {Promise<Discord.Message>}
	 */
	static async send(options, requester, channel, footerSuffix = "") {
		const optionsCpy = options;

		optionsCpy.footer = {
			icon_url: requester.avatarURL,
			text: `Requested by ${requester.username} ${footerSuffix}`
		};

		if (!optionsCpy.color) {
			// TODO: Color is literal hex, not string (gives error)
			optionsCpy.color = "GREEN";
		}

		return await channel.send({
			embed: options
		});
	}

	/**
	 * @param {Number} milliseconds
	 * @param {Number} [seconds=0]
	 * @param {Number} [minutes=0]
	 * @param {Number} [hours=0]
	 * @param {Number} [days=0]
	 * @param {Number} [months=0]
	 * @param {Number} [years=0]
	 * @returns {Number}
	 */
	static timeFromNow(milliseconds, seconds = 0, minutes = 0, hours = 0, days = 0, months = 0, years = 0) {
		const now = new Date();

		return new Date(years + now.getFullYear(), months + now.getMonth(), days + now.getDate(), hours + now.getHours(), minutes + now.getMinutes(), seconds + now.getSeconds(), milliseconds + now.getMilliseconds()).getTime();
	}

	/**
	 * @param {Number} timestamp
	 * @returns {*}
	 */
	static timeAgo(timestamp) {
		return timeAgo.format(timestamp);
	}

	/**
	 * @param {String} state
	 * @returns {Boolean}
	 */
	static translateState(state) {
		return /^(1|true|on|y|yes)$/i.test(state);
	}
}

