const Discord = require("discord.js");

export default class EmbedBuilder {
	constructor() {
		/**
		 * @type {Discord.RichEmbed}
		 * @private
		 * @readonly
		 */
		this.embed = new Discord.RichEmbed();
	}

	/**
	 * Set the color of the embed
	 * @param {String} color
	 * @returns {EmbedBuilder}
	 */
	color(color) {
		this.embed.setColor(color);

		return this;
	}

	/**
	 * Set the title of the embed
	 * @param {String} title
	 * @returns {EmbedBuilder}
	 */
	title(title) {
		this.embed.setAuthor(title, this.embed.author ? this.embed.author.icon_url : null);

		return this;
	}

	/**
	 * @param {String} url
	 * @returns {EmbedBuilder}
	 */
	titleIcon(url) {
		this.embed.setAuthor(this.embed.author ? this.embed.author.name : null, url);

		return this;
	}

	/**
	 * Set the thumbnail image of the embed
	 * @param {String} url
	 * @returns {EmbedBuilder}
	 */
	thumbnail(url) {
		this.embed.setThumbnail(url);

		return this;
	}

	/**
	 * Set the footer text of the embed
	 * @param {String} text
	 * @param {String} icon
	 * @returns {EmbedBuilder}
	 */
	footer(text, icon) {
		this.embed.setFooter(text, icon);

		return this;
	}

	/**
	 * Set the image of the embed
	 * @param {String} url
	 * @returns {EmbedBuilder}
	 */
	image(url) {
		this.embed.setImage(url);

		return this;
	}

	/**
	 * Set the text of the embed
	 * @todo Limit text to Discord's embed char limit
	 * @param {string} text
	 * @returns {EmbedBuilder}
	 */
	text(text) {
		this.embed.setDescription(text);

		return this;
	}

	/**
	 * Add a field to the embed
	 * @param {String} title
	 * @param {*} value
	 * @returns {EmbedBuilder}
	 */
	field(title, value) {
		this.embed.addField(title, value);

		return this;
	}

	/**
	 * Convert the embed to a RichEmbed
	 * @returns {Discord.RichEmbed}
	 */
	build() {
		return this.embed;
	}

	/**
	 * @param {Object} obj
	 * @returns {EmbedBuilder}
	 */
	static fromObject(obj) {
		const result = new EmbedBuilder();

		if (!obj.text) {
			throw new Error("[EmbedBuilder.fromObject] Text cannot be empty or null");
		}
		else {
			result.text(obj.text);
		}

		if (obj.image) {
			result.image(obj.image);
		}

		if (obj.color) {
			result.color(obj.color);
		}

		if (obj.title) {
			result.title(obj.title);
		}

		if (obj.titleIcon) {
			result.titleIcon(obj.titleIcon);
		}

		if (obj.footer) {
			result.footer(obj.footer.text, obj.footer.icon);
		}

		if (obj.thumbnail) {
			result.thumbnail(obj.thumbnail);
		}

		return result;
	}

	/**
	 * @param {Object} sections
	 * @param {String} color
	 * @return {EmbedBuilder}
	 */
	static sections(sections, color = null) {
		const result = new EmbedBuilder();

		for (let i = 0; i < Object.keys(sections).length; i++) {
			result.field(Object.keys(sections)[i], sections[Object.keys(sections)[i]]);
		}

		if (color) {
			result.color(color);
		}

		return result;
	}
}

export default class MessageBuilder {
	/**
	 * @param {string} [startingString=""]
	 */
	constructor(startingString = "") {
		this.message = startingString;
	}

	/**
	 * @param {string} string
	 * @returns {MessageBuilder}
	 */
	add(string) {
		this.message += string;

		return this;
	}

	/**
	 * @param {(String|Null)} language
	 * @param {(String|Null)} [code=null]
	 * @returns {MessageBuilder}
	 */
	codeBlock(language = null, code = null) {
		let result = "```";

		if (language !== null) {
			result += `${language}\n`;
		}

		if (code !== null) {
			result += `${code}\n\`\`\``;
		}

		return this.add(result);
	}

	/**
	 * @param {(string|null)} code
	 * @returns {MessageBuilder}
	 */
	code(code = null) {
		if (code === null) {
			return this.add("`");
		}

		return this.add(`\`${code}\``);
	}

	/**
	 * @param {string} text
	 * @returns {MessageBuilder}
	 */
	italic(text) {
		return this.add(`*${text}*`);
	}

	/**
	 * @param {string} text
	 * @returns {MessageBuilder}
	 */
	bold(text) {
		return this.add(`**${text}**`);
	}

	/**
	 * @param {string} text
	 * @returns {MessageBuilder}
	 */
	underlined(text) {
		return this.add(`__${text}__`);
	}

	/**
	 * @returns {MessageBuilder}
	 */
	line() {
		return this.add("\n");
	}

	/**
	 * @param {string} emoji
	 * @returns {MessageBuilder}
	 */
	emoji(emoji) {
		return this.add(`:${emoji}:`);
	}

	/**
	 * @returns {string}
	 */
	build() {
		return this.message;
	}
}

const _ = require("lodash");

export default class Schema {
	/**
	 * @param {Object} struct
	 * @param {Object} schema
	 * @returns {Boolean} Whether the schema is valid
	 */
	static validate(struct, schema) {
		const keys = Object.keys(schema);

		for (let i = 0; i < keys.length; i++) {
			if (typeof schema[keys[i]] === "string") {
				if (typeof _.get(struct, keys[i]) !== schema[keys[i]]) {
					return false;
				}
			}
			else if (typeof schema[keys[i]] === "object") {
				if (!(_.get(struct, keys[i]) instanceof schema[keys[i]])) {
					return false;
				}
			}
			else {
				throw new Error(`[Schema.validate] Expecting string or object type but got '${typeof schema[keys[i]]}'`);
			}
		}

		return true;
	}
}

